package main;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=30;i++) {
			if(i%7==0) {
			System.out.println(i);
			}
		}
		
		for(int a=1;a<=30;a++) {
			if(a%3==0 && a%5==0) {
				System.out.println(a);
			}
		}
		
		for(int b=1;b<=100;b++) {
			if(b%2==0) {
				System.out.println(b);
			}
		}
		
		
		
	}

}
